    render() {
      const {isAuthenticated,user} = this.props.auth
      const {cartItems} = this.props.cartItem;
    return (
      <div>
          <header>
              <strong>{user ? `Hello ${user.name}` : ""}</strong>
          </header>
        <ListGroup>

        </ListGroup>
      </div>
    );
  }
}

