import React, { Component } from "react";
import { ListGroup, ListGroupItem } from "reactstrap";
import { connect } from "react-redux";
import { getCartItems, deleteItemFromCart } from "../../actions/cartActions";

export class MyCart extends Component {
  componentDidMount() {
    this.props.getCartItems();
  }

  onDeleteClick = id => {
    this.props.deleteItemFromCart(id);
  };

  render() {
    const { isAuthenticated, user } = this.props.auth;

    return (
      <div>
        {user ? <h1>Hello {user.name}</h1> : null}
        {this.props.cartItems.items.map(id => {
          console.log(`This is the first` + id)
          // this.props.items.map(item => {
          //   if (item._id == id) {
          //     <p key={id}>{item.name}</p>;
          //   }
          // });
        })}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  auth: state.auth,
  cartItems: state.cartItem,
  items: state.Item
});

export default connect(
  mapStateToProps,
  { getCartItems, deleteItemFromCart }
)(MyCart);
