import React, { Component } from "react";
import { ListGroup, ListGroupItem, Button } from "reactstrap";
import { connect } from "react-redux";
import { getCartItems, deleteItemFromCart } from "../../actions/cartActions";
import { getItems } from "../../actions/itemActions";

export class MyCart extends Component {
  componentDidMount() {
    this.props.getCartItems();
    this.props.getItems();
  }

  onDeleteClick = id => {
    this.props.deleteItemFromCart(id);
  };

  render() {
    const { isAuthenticated, user } = this.props.auth;
    // const {items} = this.props.items
    return (
      <div>
        {user ? <h1>Hello {user.name}</h1> : null}
        <ListGroup>
          {this.props.cartItems.items.map(item => (
            <ListGroupItem key={item._id}>
              {item.name}
              <div style={{ float: "right" }}>{item.price} â‚ª</div>
              <Button
                className="remove-btn"
                color="danger"
                size="sm"
                float="right"
                onClick={this.onDeleteClick.bind(this, item._id)}
              >
                &times;
              </Button>
            </ListGroupItem>
          ))}
        </ListGroup>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  auth: state.auth,
  cartItems: state.cartItem,
  items: state.Item
});

export default connect(
  mapStateToProps,
  { getCartItems, deleteItemFromCart, getItems }
)(MyCart);
